# planning/models.pyfrom django.db import modelsfrom gestion.models import Moniteur, Eleve, Vehiculeclass Seance(models.Model):    moniteur = models.ForeignKey(Moniteur, on_delete=models.CASCADE)    eleve = models.ForeignKey(Eleve, on_delete=models.CASCADE)    vehicule = models.ForeignKey(Vehicule, on_delete=models.CASCADE)    start_datetime = models.DateTimeField()    end_datetime = models.DateTimeField()    def __str__(self):        return (f"{self.eleve.nom} avec {self.moniteur.nom} "                f"du {self.start_datetime} au {self.end_datetime}")